package Begginer;

public class loopunderstanding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub





		//for loop
		//Pre-conditional loop
		for(int i=0;i<=10;i++) {
			System.out.println("Loop #" +  i);	
			System.out.println("my name is jay");

		}

		int i=0;
		do {
			System.out.println("Loop #" +  i);	
			System.out.println("my name is jay");	
			i++;
		} while(i<10);


	}		

}